class AgregarRenderer {
    // gets called once before the renderer is used
    init(params) {
        // create the cell
        this.eGui = document.createElement('div');
        this.eGui.innerHTML = `
          <span>
              <button class="btn btn-simple">Agregar</button>
          </span>
       `;

        // get references to the elements we want
        this.eButton = this.eGui.querySelector('.btn-simple');
        this.eValue = this.eGui.querySelector('.my-value');

        // set value into cell
        //this.cellValue = " ";
        //this.eValue.innerHTML = this.cellValue;

        // add event listener to button
        //this.eventListener = () => alert(`${this.cellValue} medals won!`);
        this.eventListener = () => agregar(params.data.nombreadd, params.data.claveadd);
        this.eButton.addEventListener('click', this.eventListener);
    }

    getGui() {
        return this.eGui;
    }

    // gets called whenever the cell refreshes
    refresh(params) {
        // set value into cell again
        this.cellValue = this.getValueToDisplay(params);
        this.eValue.innerHTML = this.cellValue;

        // return true to tell the grid we refreshed successfully
        return true;
    }

    // gets called when the cell is removed from the grid
    destroy() {
        // do cleanup, remove event listener from button
        if (this.eButton) {
            // check that the button element exists as destroy() can be called before getGui()
            this.eButton.removeEventListener('click', this.eventListener);
        }
    }

    getValueToDisplay(params) {
        return params.data.nombre ? params.data.nombre : "";
    }
}

function modificar(id, nombre, clave) {
    // console.log("ID: " + id + " Nombre: " + nombre);
    //agregar la modificación con un ajax.
    var prd = document.getElementById("cuerpo");

    $.ajax({
        type: "POST",
        url: "Ajax/modificarCliente.aspx",
        data: { "clave": clave, "idCliente": id, "nombre": nombre },
        async: true,
        success: function (data) {
            // $("#repetido").val(data);
            if (data != "") {
                prd.innerHTML = data;
            }
            else {
                prd.innerHTML = "ID: " + id + " Nombre: " + nombre + " Clave: " + clave;
            }
            $("#myModalMensaje").modal("show");
        },
        error: function (XMLHttpRequest) {
            var error = eval(XMLHttpRequest.responseText);
            console(error.Message);
            prd.innerHTML = error.Message;
            $("#myModalMensaje").modal("show");
        }
    });

}


